import React, { useEffect } from 'react';
import { useSelector } from 'react-redux';
import { RootState } from '../../store';
import { useJobs } from '../../hooks/useJobs';
import JobList from './JobList';
import JobFilters from './JobFilters';
import LoadingSpinner from '../common/LoadingSpinner';

const JobsBoard: React.FC = () => {
  const { user } = useSelector((state: RootState) => state.auth);
  const { jobs, loading, filters, loadJobs, updateFilters } = useJobs();

  // Simple load on mount
  useEffect(() => {
    console.log('Loading jobs...');
    loadJobs({ page: 1, pageSize: 10 });
  }, [loadJobs]);

  const handleSearch = (search: string) => {
    console.log('Search:', search);
    updateFilters({ search });
  };

  const handleStatusFilter = (status: string) => {
    console.log('Status:', status);
    updateFilters({ status });
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Jobs Board</h1>
        <p className="text-gray-600 mb-8">
          {user?.role === 'hr' 
            ? 'HR Portal - Jobs Management' 
            : 'Candidate View - Browse Jobs'
          }
        </p>
        
        <div className="flex gap-8">
          {/* Filters Sidebar */}
          <div className="w-80 flex-shrink-0">
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-semibold mb-4">Filters</h3>
              <JobFilters
                searchValue={filters.search}
                statusValue={filters.status}
                onSearch={handleSearch}
                onStatusChange={handleStatusFilter}
              />
            </div>
          </div>

          {/* Jobs List */}
          <div className="flex-1">
            <div className="bg-white rounded-lg shadow p-6">
              <h2 className="text-xl font-semibold mb-4">Jobs List</h2>
              
              {loading ? (
                <div className="flex justify-center py-8">
                  <LoadingSpinner size="lg" />
                </div>
              ) : (
                <div>
                  <p className="text-gray-600 mb-4">
                    Loaded {jobs.length} jobs
                  </p>
                  
                  <JobList jobs={jobs} />
                  
                  <div className="mt-4 p-4 bg-green-50 border border-green-200 rounded">
                    <p className="text-green-800">âœ“ Filters and JobList working</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JobsBoard;
