import React from 'react';
import {
  SortableContext,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';
import { Candidate } from '../../types';
import KanbanCandidateCard from './KanbanCandidateCard';

interface StageColumnProps {
  stage: Candidate['stage'];
  candidates: Candidate[];
  stageLabel: string;
  stageColor: string;
}

const StageColumn: React.FC<StageColumnProps> = ({
  stage,
  candidates,
  stageLabel,
  stageColor,
}) => {
  return (
    <div className="flex-1 min-w-[300px] bg-gray-50 rounded-lg border border-gray-200">
      {/* Column Header */}
      <div className={`p-4 rounded-t-lg ${stageColor} text-white`}>
        <div className="flex justify-between items-center">
          <h3 className="font-semibold">{stageLabel}</h3>
          <span className="bg-white bg-opacity-20 px-2 py-1 rounded-full text-sm">
            {candidates.length}
          </span>
        </div>
      </div>

      {/* Droppable Area */}
      <SortableContext items={candidates.map(c => c.id)} strategy={verticalListSortingStrategy}>
        <div className="p-2 min-h-[500px]">
          {candidates.map((candidate) => (
            <KanbanCandidateCard key={candidate.id} candidate={candidate} />
          ))}
          {candidates.length === 0 && (
            <div className="text-center text-gray-500 py-8">
              No candidates
            </div>
          )}
        </div>
      </SortableContext>
    </div>
  );
};

export default StageColumn;
