import React from 'react';

interface JobFiltersProps {
  filters: {
    search: string;
    status: string;
    tags: string[];
    page: number;
    pageSize: number;
    sort: string;
  };
  onFiltersChange: (filters: any) => void;
}

const JobFilters: React.FC<JobFiltersProps> = ({ filters, onFiltersChange }) => {
  const popularTags = ['React', 'Remote', 'Frontend', 'Full Stack', 'Node.js', 'Python', 'AWS', 'UI/UX'];

  const handleTagToggle = (tag: string) => {
    const newTags = filters.tags.includes(tag)
      ? filters.tags.filter(t => t !== tag)
      : [...filters.tags, tag];
    
    onFiltersChange({ ...filters, tags: newTags, page: 1 });
  };

  const clearAllFilters = () => {
    onFiltersChange({
      search: '',
      status: '',
      tags: [],
      page: 1,
      pageSize: 12,
      sort: 'createdAt_desc'
    });
  };

  const hasActiveFilters = filters.search || filters.status || filters.tags.length > 0;

  return (
    <div className="bg-white/60 backdrop-blur-sm rounded-2xl border border-gray-200/60 p-6 shadow-soft">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-lg font-semibold text-gray-900">Filters</h3>
        {hasActiveFilters && (
          <button
            onClick={clearAllFilters}
            className="text-sm text-blue-600 hover:text-blue-700 font-medium"
          >
            Clear all
          </button>
        )}
      </div>

      {/* Search */}
      <div className="mb-6">
        <label htmlFor="search" className="block text-sm font-medium text-gray-700 mb-2">
          Search Jobs
        </label>
        <div className="relative">
          <input
            type="text"
            id="search"
            value={filters.search}
            onChange={(e) => onFiltersChange({ ...filters, search: e.target.value, page: 1 })}
            placeholder="Job title, company, keywords..."
            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 bg-white/60 backdrop-blur-sm"
          />
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <svg className="h-5 w-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
          </div>
        </div>
      </div>

      {/* Status Filter */}
      <div className="mb-6">
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Status
        </label>
        <div className="space-y-2">
          {['active', 'archived'].map((status) => (
            <label key={status} className="flex items-center">
              <input
                type="radio"
                name="status"
                value={status}
                checked={filters.status === status}
                onChange={(e) => onFiltersChange({ ...filters, status: e.target.value, page: 1 })}
                className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300"
              />
              <span className="ml-2 text-sm text-gray-700 capitalize">
                {status}
              </span>
            </label>
          ))}
          <label className="flex items-center">
            <input
              type="radio"
              name="status"
              value=""
              checked={filters.status === ''}
              onChange={(e) => onFiltersChange({ ...filters, status: e.target.value, page: 1 })}
              className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300"
            />
            <span className="ml-2 text-sm text-gray-700">All Status</span>
          </label>
        </div>
      </div>

      {/* Tags Filter */}
      <div className="mb-6">
        <label className="block text-sm font-medium text-gray-700 mb-3">
          Popular Tags
        </label>
        <div className="flex flex-wrap gap-2">
          {popularTags.map((tag) => {
            const isSelected = filters.tags.includes(tag);
            return (
              <button
                key={tag}
                onClick={() => handleTagToggle(tag)}
                className={`
                  inline-flex items-center px-3 py-1.5 rounded-full text-xs font-medium border transition-all duration-200
                  ${isSelected
                    ? 'bg-blue-100 text-blue-800 border-blue-200 shadow-sm'
                    : 'bg-gray-100 text-gray-700 border-gray-200 hover:bg-gray-200'
                  }
                `}
              >
                {tag}
                {isSelected && (
                  <svg className="w-3 h-3 ml-1" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                  </svg>
                )}
              </button>
            );
          })}
        </div>
      </div>

      {/* Results Count */}
      <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-lg p-4 border border-blue-200">
        <p className="text-sm text-blue-800">
          <strong>Tip:</strong> Use multiple filters to narrow down your search results and find the perfect match.
        </p>
      </div>
    </div>
  );
};

export default JobFilters;
