import React, { useEffect, useState } from 'react';
import { useCandidates } from '../../hooks/useCandidates';
import VirtualizedList from './VirtualizedList';
import CandidateFilters from './CandidateFilters';
import KanbanBoard from './KanbanBoard';
import LoadingSpinner from '../common/LoadingSpinner';

type ViewMode = 'list' | 'kanban';

const CandidatesList: React.FC = () => {
  const {
    candidates,
    loading,
    pagination,
    filters,
    loadCandidates,
    updateFilters,
  } = useCandidates();

  const [viewMode, setViewMode] = useState<ViewMode>('list');
  const [searchTerm, setSearchTerm] = useState(filters.search);

  useEffect(() => {
    loadCandidates({
      page: pagination.page,
      pageSize: pagination.pageSize,
      search: filters.search,
      stage: filters.stage,
    });
  }, [pagination.page, pagination.pageSize, filters.search, filters.stage]);

  const handleSearch = (search: string) => {
    setSearchTerm(search);
    updateFilters({ search });
  };

  const handleStageFilter = (stage: string) => {
    updateFilters({ stage });
  };

  const handleViewModeChange = (mode: ViewMode) => {
    setViewMode(mode);
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Candidates</h1>
          <p className="text-gray-600">Manage and track candidate applications</p>
        </div>
        <div className="flex items-center space-x-4">
          {/* View Mode Toggle */}
          <div className="flex bg-gray-100 rounded-lg p-1">
            <button
              onClick={() => handleViewModeChange('list')}
              className={`px-3 py-2 text-sm font-medium rounded-md ${
                viewMode === 'list'
                  ? 'bg-white text-gray-900 shadow-sm'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              List View
            </button>
            <button
              onClick={() => handleViewModeChange('kanban')}
              className={`px-3 py-2 text-sm font-medium rounded-md ${
                viewMode === 'kanban'
                  ? 'bg-white text-gray-900 shadow-sm'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              Kanban Board
            </button>
          </div>
        </div>
      </div>

      {/* Filters */}
      <CandidateFilters
        searchValue={searchTerm}
        stageValue={filters.stage}
        onSearch={handleSearch}
        onStageChange={handleStageFilter}
      />

      {/* Candidates List/Kanban */}
      <div className="bg-white shadow-sm rounded-lg border border-gray-200">
        {loading ? (
          <div className="flex justify-center items-center py-12">
            <LoadingSpinner size="lg" />
          </div>
        ) : viewMode === 'list' ? (
          <VirtualizedList
            candidates={candidates}
            height={600}
            itemHeight={80}
          />
        ) : (
          <KanbanBoard candidates={candidates} />
        )}
      </div>

      {/* Results Count */}
      <div className="text-sm text-gray-600">
        Showing {candidates.length} of {pagination.total} candidates
      </div>
    </div>
  );
};

export default CandidatesList;
