import React from 'react';
import { useSelector } from 'react-redux';
import { RootState } from '../../store';

const JobsBoard: React.FC = () => {
  const { user } = useSelector((state: RootState) => state.auth);

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">Jobs Board</h1>
        <p className="text-gray-600 mb-8">
          {user?.role === 'hr' 
            ? 'HR Portal - Jobs Management' 
            : 'Candidate View - Browse Jobs'
          }
        </p>
        
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold mb-4">Minimal Jobs Board</h2>
          <p className="text-gray-600">
            This is a minimal version. If this works, the issue is in the hooks or child components.
          </p>
          <div className="mt-4 p-4 bg-green-50 border border-green-200 rounded">
            <p className="text-green-800">âœ“ Component is rendering correctly</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JobsBoard;
