import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAppDispatch } from '../../hooks/redux';
import { login } from '../../store/slices/authSlice';

interface LoginCredentials {
  email: string;
  password: string;
  role: 'hr' | 'candidate';
}

const Login: React.FC = () => {
  const dispatch = useAppDispatch();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [activeTab, setActiveTab] = useState<'hr' | 'candidate'>('candidate');
  const [credentials, setCredentials] = useState<LoginCredentials>({
    email: '',
    password: '',
    role: 'candidate'
  });
  const [showPassword, setShowPassword] = useState(false);
  const [shake, setShake] = useState(false);

  // Predefined credentials
  const validCredentials = {
    hr: [
      { email: 'hr@talentflow.com', password: 'hr123' },
      { email: 'admin@talentflow.com', password: 'admin123' },
      { email: 'recruiter@company.com', password: 'recruiter123' }
    ],
    candidate: [
      { email: 'candidate@talentflow.com', password: 'candidate123' },
      { email: 'john.doe@email.com', password: 'john123' },
      { email: 'sarah.wilson@email.com', password: 'sarah123' },
      { email: 'demo@candidate.com', password: 'demo123' }
    ]
  };

  useEffect(() => {
    // Auto-fill demo credentials when tab changes
    const demoCredential = validCredentials[activeTab][0];
    setCredentials(prev => ({
      ...prev,
      email: demoCredential.email,
      password: demoCredential.password,
      role: activeTab
    }));
  }, [activeTab]);

  const handleInputChange = (field: keyof LoginCredentials, value: string) => {
    setCredentials(prev => ({ ...prev, [field]: value }));
  };

  const validateCredentials = (email: string, password: string, role: 'hr' | 'candidate'): boolean => {
    return validCredentials[role].some(cred => 
      cred.email === email && cred.password === password
    );
  };

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 800));

    const isValid = validateCredentials(credentials.email, credentials.password, credentials.role);
    
    if (isValid) {
      setTimeout(() => {
        if (credentials.role === 'hr') {
          dispatch(login({ 
            role: 'hr',
            userId: 'hr-1'
          }));
          navigate('/jobs');
        } else {
          const candidateId = `candidate-${Math.floor(Math.random() * 1000) + 1}`;
          dispatch(login({ 
            role: 'candidate', 
            userId: candidateId
          }));
          navigate('/candidate-dashboard');
        }
        setIsLoading(false);
      }, 400);
    } else {
      setShake(true);
      setIsLoading(false);
      setTimeout(() => setShake(false), 500);
    }
  };

  const handleQuickLogin = (role: 'hr' | 'candidate', index: number = 0) => {
    const credential = validCredentials[role][index];
    setCredentials({
      email: credential.email,
      password: credential.password,
      role
    });
    setActiveTab(role);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-slate-900 via-blue-900 to-purple-900 relative overflow-hidden">
      {/* Enhanced Animated Background */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-blue-500/10 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-purple-500/10 rounded-full blur-3xl animate-pulse delay-1000"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-96 h-96 bg-cyan-500/5 rounded-full blur-3xl animate-pulse delay-500"></div>
        
        {/* Enhanced Grid Pattern */}
        <div className="absolute inset-0 bg-[linear-gradient(rgba(255,255,255,0.02)_1px,transparent_1px),linear-gradient(90deg,rgba(255,255,255,0.02)_1px,transparent_1px)] bg-[size:4rem_4rem] [mask-image:radial-gradient(ellipse_80%_50%_at_50%_50%,black,transparent)]"></div>
        
        {/* Floating Elements */}
        <div className="absolute top-20 left-20 w-4 h-4 bg-blue-400/20 rounded-full animate-bounce"></div>
        <div className="absolute bottom-32 right-32 w-6 h-6 bg-purple-400/20 rounded-full animate-bounce delay-300"></div>
        <div className="absolute top-1/3 right-1/4 w-3 h-3 bg-cyan-400/20 rounded-full animate-bounce delay-700"></div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 w-full max-w-4xl mx-4">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 items-center">
          {/* Left Side - Branding & Demo Credentials */}
          <div className="text-center lg:text-left space-y-8">
            {/* Logo & Brand */}
            <div className="space-y-4">
              <div className="inline-flex items-center justify-center lg:justify-start space-x-3">
                <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-cyan-500 rounded-xl flex items-center justify-center shadow-lg shadow-blue-500/25">
                  <span className="text-white font-bold text-lg">TF</span>
                </div>
                <h1 className="text-3xl font-bold text-white">TalentFlow</h1>
              </div>
              <p className="text-xl text-blue-200 font-light">
                Next Generation Hiring Platform
              </p>
            </div>

            {/* Demo Credentials - Same size as original Why TalentFlow section */}
            <div className="bg-white/5 backdrop-blur-xl rounded-2xl border border-white/10 p-6 space-y-6">
              <h3 className="text-xl font-semibold text-white text-center lg:text-left">Demo Credentials</h3>
              
              {/* HR Credentials */}
              <div className="space-y-3">
                <h4 className="text-sm font-semibold text-white flex items-center">
                  <span className="w-2 h-2 bg-blue-400 rounded-full mr-2"></span>
                  HR Team
                </h4>
                <div className="space-y-2">
                  {validCredentials.hr.slice(0, 2).map((cred, idx) => (
                    <div key={idx} className="bg-white/5 rounded-lg p-3 border border-white/10 hover:border-blue-400/30 transition-all duration-300">
                      <div className="text-xs text-blue-200 space-y-1">
                        <div>Email: {cred.email}</div>
                        <div>Password: {cred.password}</div>
                      </div>
                      <button
                        onClick={() => handleQuickLogin('hr', idx)}
                        className="w-full mt-2 bg-blue-500/20 hover:bg-blue-500/30 text-blue-300 hover:text-white py-1 rounded text-xs font-medium transition-all duration-300"
                      >
                        Use This
                      </button>
                    </div>
                  ))}
                </div>
              </div>

              {/* Candidate Credentials */}
              <div className="space-y-3">
                <h4 className="text-sm font-semibold text-white flex items-center">
                  <span className="w-2 h-2 bg-emerald-400 rounded-full mr-2"></span>
                  Candidate
                </h4>
                <div className="space-y-2">
                  {validCredentials.candidate.slice(0, 2).map((cred, idx) => (
                    <div key={idx} className="bg-white/5 rounded-lg p-3 border border-white/10 hover:border-emerald-400/30 transition-all duration-300">
                      <div className="text-xs text-blue-200 space-y-1">
                        <div>Email: {cred.email}</div>
                        <div>Password: {cred.password}</div>
                      </div>
                      <button
                        onClick={() => handleQuickLogin('candidate', idx)}
                        className="w-full mt-2 bg-emerald-500/20 hover:bg-emerald-500/30 text-emerald-300 hover:text-white py-1 rounded text-xs font-medium transition-all duration-300"
                      >
                        Use This
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Feature Highlights - Kept for balance */}
            <div className="space-y-4">
              <div className="flex items-center space-x-3 text-blue-100">
                <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
                <span className="text-sm">Smart Candidate Matching</span>
              </div>
              <div className="flex items-center space-x-3 text-blue-100">
                <div className="w-2 h-2 bg-blue-400 rounded-full animate-pulse delay-300"></div>
                <span className="text-sm">Automated Assessments</span>
              </div>
              <div className="flex items-center space-x-3 text-blue-100">
                <div className="w-2 h-2 bg-purple-400 rounded-full animate-pulse delay-700"></div>
                <span className="text-sm">Real-time Collaboration</span>
              </div>
            </div>
          </div>

          {/* Right Side - Login Form - EXACTLY AS BEFORE */}
          <div className="space-y-6">
            {/* Header - Updated Welcome Back to Welcome Back! */}
            <div className="text-center lg:text-left">
              <h2 className="text-3xl font-bold text-white mb-2">
                Welcome Back!
              </h2>
              <p className="text-blue-200">
                Sign in to access your portal
              </p>
            </div>

            {/* Login Cards - EXACTLY AS BEFORE */}
            <div className="space-y-4">
              {/* HR Login Card */}
              <div 
                className={`group relative bg-white/10 backdrop-blur-xl rounded-2xl border border-white/20 p-6 transition-all duration-500 hover:bg-white/15 hover:border-blue-400/50 hover:shadow-2xl hover:shadow-blue-500/20 ${
                  isLoading === 'hr' ? 'animate-pulse' : ''
                }`}
              >
                {/* Glow Effect */}
                <div className="absolute inset-0 bg-gradient-to-r from-blue-500/10 to-cyan-500/10 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
                
                <div className="relative z-10">
                  <div className="flex items-center space-x-4 mb-4">
                    <div className="w-14 h-14 bg-gradient-to-r from-blue-500 to-cyan-500 rounded-xl flex items-center justify-center shadow-lg shadow-blue-500/25 group-hover:scale-110 transition-transform duration-300">
                      <svg className="w-7 h-7 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2 2v2m4 6h.01M5 20h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                      </svg>
                    </div>
                    <div className="flex-1">
                      <h3 className="text-lg font-semibold text-white">HR Team</h3>
                      <p className="text-sm text-blue-200">Manage jobs, candidates & assessments</p>
                    </div>
                  </div>
                  <button
                    onClick={() => {
                      setIsLoading('hr');
                      setTimeout(() => {
                        dispatch(login({ role: 'hr' }));
                        navigate('/jobs');
                        setIsLoading(null);
                      }, 1200);
                    }}
                    disabled={isLoading !== null}
                    className="w-full bg-gradient-to-r from-blue-600 to-cyan-600 hover:from-blue-700 hover:to-cyan-700 text-white py-3 px-4 rounded-xl font-medium focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-slate-900 transition-all duration-300 transform hover:scale-105 disabled:opacity-50 disabled:transform-none group relative overflow-hidden"
                  >
                    <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent -skew-x-12 -translate-x-full group-hover:translate-x-full transition-transform duration-1000"></div>
                    {isLoading === 'hr' ? (
                      <div className="flex items-center justify-center space-x-2">
                        <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                        <span>Initializing Portal...</span>
                      </div>
                    ) : (
                      <span className="relative">Access HR Portal</span>
                    )}
                  </button>
                </div>
              </div>

              {/* Candidate Login Card */}
              <div 
                className={`group relative bg-white/10 backdrop-blur-xl rounded-2xl border border-white/20 p-6 transition-all duration-500 hover:bg-white/15 hover:border-emerald-400/50 hover:shadow-2xl hover:shadow-emerald-500/20 ${
                  isLoading === 'candidate' ? 'animate-pulse' : ''
                }`}
              >
                {/* Glow Effect */}
                <div className="absolute inset-0 bg-gradient-to-r from-emerald-500/10 to-green-500/10 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
                
                <div className="relative z-10">
                  <div className="flex items-center space-x-4 mb-4">
                    <div className="w-14 h-14 bg-gradient-to-r from-emerald-500 to-green-500 rounded-xl flex items-center justify-center shadow-lg shadow-emerald-500/25 group-hover:scale-110 transition-transform duration-300">
                      <svg className="w-7 h-7 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                      </svg>
                    </div>
                    <div className="flex-1">
                      <h3 className="text-lg font-semibold text-white">Candidate</h3>
                      <p className="text-sm text-blue-200">Browse jobs & submit applications</p>
                    </div>
                  </div>
                  <button
                    onClick={() => {
                      setIsLoading('candidate');
                      setTimeout(() => {
                        const candidateId = `candidate-${Math.floor(Math.random() * 1000) + 1}`;
                        dispatch(login({ role: 'candidate', userId: candidateId }));
                        navigate('/candidate-dashboard');
                        setIsLoading(null);
                      }, 1200);
                    }}
                    disabled={isLoading !== null}
                    className="w-full bg-gradient-to-r from-emerald-600 to-green-600 hover:from-emerald-700 hover:to-green-700 text-white py-3 px-4 rounded-xl font-medium focus:outline-none focus:ring-2 focus:ring-emerald-500 focus:ring-offset-2 focus:ring-offset-slate-900 transition-all duration-300 transform hover:scale-105 disabled:opacity-50 disabled:transform-none group relative overflow-hidden"
                  >
                    <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent -skew-x-12 -translate-x-full group-hover:translate-x-full transition-transform duration-1000"></div>
                    {isLoading === 'candidate' ? (
                      <div className="flex items-center justify-center space-x-2">
                        <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                        <span>Entering Platform...</span>
                      </div>
                    ) : (
                      <span className="relative">Enter as Candidate</span>
                    )}
                  </button>
                </div>
              </div>
            </div>

            {/* Demo Info */}
            <div className="bg-white/5 backdrop-blur-xl border border-white/10 rounded-2xl p-6 transition-all duration-300 hover:bg-white/10 hover:border-white/20">
              <div className="flex items-start space-x-3">
