import React from 'react';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { Link } from 'react-router-dom';
import { Candidate } from '../../types';

interface KanbanCandidateCardProps {
  candidate: Candidate;
}

const KanbanCandidateCard: React.FC<KanbanCandidateCardProps> = ({ candidate }) => {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: candidate.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  const getInitials = (name: string) => {
    return name.split(' ').map(n => n[0]).join('').toUpperCase();
  };

  const getStageColor = (stage: Candidate['stage']) => {
    const colors = {
      applied: 'bg-blue-100 text-blue-800',
      screen: 'bg-yellow-100 text-yellow-800',
      tech: 'bg-purple-100 text-purple-800',
      offer: 'bg-green-100 text-green-800',
      hired: 'bg-emerald-100 text-emerald-800',
      rejected: 'bg-red-100 text-red-800',
    };
    return colors[stage];
  };

  return (
    <div
      ref={setNodeRef}
      style={style}
      {...attributes}
      {...listeners}
      className={`bg-white/90 backdrop-blur-sm rounded-2xl p-4 border border-gray-200/60 shadow-soft hover:shadow-lg transition-all duration-300 mb-3 cursor-grab active:cursor-grabbing group ${
        isDragging ? 'opacity-50 rotate-3 shadow-xl' : 'hover:-translate-y-1'
      }`}
    >
      <div className="flex items-start space-x-3">
        {/* Avatar */}
        <div className="flex-shrink-0">
          <div className="h-10 w-10 bg-gradient-to-r from-blue-500 to-purple-500 rounded-xl flex items-center justify-center text-white font-bold text-sm">
            {getInitials(candidate.name)}
          </div>
        </div>
        
        {/* Candidate Info */}
        <div className="flex-1 min-w-0">
          <div className="flex items-start justify-between">
            <div className="flex-1">
              <Link
                to={`/candidates/${candidate.id}`}
                className="text-sm font-semibold text-gray-900 hover:text-blue-600 truncate block transition-colors duration-150"
              >
                {candidate.name}
              </Link>
              <p className="text-xs text-gray-500 truncate mt-1">
                {candidate.email}
              </p>
              
              {/* Job Title */}
              {candidate.jobTitle && (
                <p className="text-xs text-gray-700 font-medium mt-2">
                  {candidate.jobTitle}
                </p>
              )}
              
              {/* Skills */}
              {candidate.skills && candidate.skills.length > 0 && (
                <div className="mt-2 flex flex-wrap gap-1">
                  {candidate.skills.slice(0, 2).map((skill, index) => (
                    <span
                      key={index}
                      className="inline-flex items-center px-1.5 py-0.5 rounded text-xs font-medium bg-blue-50 text-blue-700 border border-blue-200"
                    >
                      {skill}
                    </span>
                  ))}
                  {candidate.skills.length > 2 && (
                    <span className="inline-flex items-center px-1.5 py-0.5 rounded text-xs font-medium bg-gray-50 text-gray-700 border border-gray-200">
                      +{candidate.skills.length - 2}
                    </span>
                  )}
                </div>
              )}
            </div>
          </div>

          {/* Footer */}
          <div className="mt-3 flex items-center justify-between">
            <div className="flex items-center text-xs text-gray-500">
              <svg className="flex-shrink-0 mr-1 h-3 w-3 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
              </svg>
              {new Date(candidate.appliedDate).toLocaleDateString()}
            </div>
            
            <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getStageColor(candidate.stage)}`}>
              {candidate.stage}
            </span>
          </div>
        </div>
      </div>

      {/* Drag Handle */}
      <div className="mt-3 pt-3 border-t border-gray-200/60 flex items-center justify-center text-xs text-gray-400 group-hover:text-gray-600 transition-colors duration-150">
        <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16V4m0 0L3 8m4-4l4 4m6 0v12m0 0l4-4m-4 4l-4-4" />
        </svg>
        Drag to move
      </div>
    </div>
  );
};

export default KanbanCandidateCard;
