import React from 'react';
import { Link } from 'react-router-dom';
import { Candidate } from '../../types';

interface KanbanCandidateCardProps {
  candidate: Candidate;
}

const KanbanCandidateCard: React.FC<KanbanCandidateCardProps> = ({ candidate }) => {
  return (
    <div className="bg-white p-3 rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition-shadow">
      <div className="flex items-start space-x-3">
        <div className="flex-shrink-0">
          <div className="h-8 w-8 bg-gray-300 rounded-full flex items-center justify-center">
            <span className="text-xs font-medium text-gray-700">
              {candidate.name.split(' ').map(n => n[0]).join('')}
            </span>
          </div>
        </div>
        <div className="flex-1 min-w-0">
          <Link
            to={`/candidates/${candidate.id}`}
            className="text-sm font-medium text-gray-900 hover:text-blue-600 truncate block"
          >
            {candidate.name}
          </Link>
          <p className="text-xs text-gray-500 truncate mt-1">
            {candidate.email}
          </p>
          <div className="mt-2 flex items-center text-xs text-gray-500">
            <svg className="flex-shrink-0 mr-1 h-3 w-3 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
            {new Date(candidate.appliedDate).toLocaleDateString()}
          </div>
        </div>
      </div>
      
      {/* Drag handle indicator */}
      <div className="mt-2 flex justify-center">
        <div className="h-1 w-6 bg-gray-300 rounded"></div>
      </div>
    </div>
  );
};

export default KanbanCandidateCard;
