import React from 'react';
import { useSelector } from 'react-redux';
import { RootState } from '../../store';
import { useJobs } from '../../hooks/useJobs';
import LoadingSpinner from '../common/LoadingSpinner';

const JobsBoard: React.FC = () => {
  const { user } = useSelector((state: RootState) => state.auth);
  const { jobs, loading, loadJobs } = useJobs();

  // Simple load on mount
  React.useEffect(() => {
    console.log('Loading jobs...');
    loadJobs({ page: 1, pageSize: 10 });
  }, [loadJobs]);

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">Jobs Board</h1>
        <p className="text-gray-600 mb-8">
          {user?.role === 'hr' 
            ? 'HR Portal - Jobs Management' 
            : 'Candidate View - Browse Jobs'
          }
        </p>
        
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold mb-4">Jobs Board with useJobs Hook</h2>
          
          {loading ? (
            <div className="flex justify-center py-8">
              <LoadingSpinner size="lg" />
            </div>
          ) : (
            <div>
              <p className="text-gray-600 mb-4">
                Loaded {jobs.length} jobs
              </p>
              <div className="mt-4 p-4 bg-green-50 border border-green-200 rounded">
                <p className="text-green-800">âœ“ useJobs hook is working</p>
                <p className="text-green-700 text-sm mt-2">
                  Jobs: {jobs.length} | Loading: {loading.toString()}
                </p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default JobsBoard;
