import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Assessment, AssessmentResponse } from '../../types';
import { useAssessments } from '../../hooks/useAssessments';
import { useFormValidation } from '../../hooks/useFormValidation';
import QuestionRenderer from './QuestionRenderer';
import LoadingSpinner from '../common/LoadingSpinner';

const AssessmentForm: React.FC = () => {
  const { jobId } = useParams<{ jobId: string }>();
  const navigate = useNavigate();
  const {
    currentAssessment,
    currentResponse,
    loading,
    validationErrors,
    loadAssessment,
    submitResponse,
    handleUpdateResponse,
    handleSetValidationError,
    handleClearValidationError,
    handleClearAllValidationErrors,
  } = useAssessments();
  
  const { validateAllQuestions } = useFormValidation();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [currentSection, setCurrentSection] = useState(0);

  useEffect(() => {
    if (jobId) {
      loadAssessment(jobId);
    }
  }, [jobId, loadAssessment]);

  useEffect(() => {
    if (currentAssessment && !currentResponse) {
      const initialResponse: AssessmentResponse = {
        id: `response-${Date.now()}`,
        assessmentId: currentAssessment.id,
        candidateId: 'current-candidate', // This would come from auth in a real app
        responses: {},
        submittedAt: undefined,
      };
      // handleSetResponse(initialResponse);
    }
  }, [currentAssessment, currentResponse]);

  const handleAnswerChange = (questionId: string, value: any) => {
    handleUpdateResponse(questionId, value);
    handleClearValidationError(questionId);
  };

  const validateCurrentSection = (): boolean => {
    if (!currentAssessment || currentSection >= currentAssessment.sections.length) return true;

    const currentSectionQuestions = currentAssessment.sections[currentSection].questions;
    const errors = validateAllQuestions(currentSectionQuestions, currentResponse?.responses || {});
    
    Object.entries(errors).forEach(([questionId, error]) => {
      handleSetValidationError(questionId, error);
    });

    return Object.keys(errors).length === 0;
  };

  const handleNextSection = () => {
    if (validateCurrentSection()) {
      setCurrentSection(prev => Math.min(prev + 1, (currentAssessment?.sections.length || 1) - 1));
    }
  };

  const handlePreviousSection = () => {
    setCurrentSection(prev => Math.max(prev - 1, 0));
  };

  const handleSubmit = async () => {
    if (!currentAssessment || !currentResponse) return;

    handleClearAllValidationErrors();
    
    const allQuestions = currentAssessment.sections.flatMap(section => section.questions);
    const errors = validateAllQuestions(allQuestions, currentResponse.responses);
    
    if (Object.keys(errors).length > 0) {
      Object.entries(errors).forEach(([questionId, error]) => {
        handleSetValidationError(questionId, error);
      });
      // Scroll to first error
      const firstErrorElement = document.querySelector('[data-error="true"]');
      firstErrorElement?.scrollIntoView({ behavior: 'smooth' });
      return;
    }

    setIsSubmitting(true);
    try {
      await submitResponse({
        ...currentResponse,
        submittedAt: new Date(),
      });
      // Navigate to success page or show success message
      navigate('/assessment/success');
    } catch (error) {
      console.error('Failed to submit assessment:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (!currentAssessment) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-gray-900">Assessment Not Found</h1>
          <p className="text-gray-600 mt-2">The assessment you're looking for doesn't exist.</p>
        </div>
      </div>
    );
  }

  const currentSectionData = currentAssessment.sections[currentSection];
  const isLastSection = currentSection === currentAssessment.sections.length - 1;

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-4xl mx-auto py-8 px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-6">
          <h1 className="text-2xl font-bold text-gray-900 mb-2">{currentAssessment.title}</h1>
          {currentAssessment.description && (
            <p className="text-gray-600">{currentAssessment.description}</p>
          )}
          
          {/* Progress */}
          <div className="mt-4">
            <div className="flex items-center justify-between text-sm text-gray-600 mb-2">
              <span>Section {currentSection + 1} of {currentAssessment.sections.length}</span>
              <span>{Math.round(((currentSection + 1) / currentAssessment.sections.length) * 100)}% Complete</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div
                className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                style={{ width: `${((currentSection + 1) / currentAssessment.sections.length) * 100}%` }}
              />
            </div>
          </div>
        </div>

        {/* Current Section */}
        {currentSectionData && (
          <div className="bg-white rounded-lg shadow-sm border border-gray-200">
            {/* Section Header */}
            <div className="px-6 py-4 border-b border-gray-200 bg-gray-50">
              <h2 className="text-lg font-medium text-gray-900">
                {currentSection + 1}. {currentSectionData.title}
              </h2>
            </div>

            {/* Questions */}
            <div className="p-6 space-y-6">
              {currentSectionData.questions.map((question, questionIndex) => (
                <div
                  key={question.id}
                  data-error={!!validationErrors[question.id]}
                  className={validationErrors[question.id] ? 'border-l-4 border-red-400 pl-4' : ''}
                >
                  <QuestionRenderer
                    question={question}
                    questionNumber={questionIndex + 1}
                    isPreview={false}
                    onChange={(value) => handleAnswerChange(question.id, value)}
                    value={currentResponse?.responses[question.id] || null}
                  />
                  
                  {validationErrors[question.id] && (
                    <p className="mt-1 text-sm text-red-600">{validationErrors[question.id]}</p>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Navigation */}
        <div className="mt-6 flex justify-between">
          <button
            onClick={handlePreviousSection}
            disabled={currentSection === 0}
            className="px-6 py-3 text-base font-medium text-gray-700 bg-white border border-gray-300 rounded-md shadow-sm hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            Previous
          </button>

          {!isLastSection ? (
            <button
              onClick={handleNextSection}
              className="px-6 py-3 text-base font-medium text-white bg-blue-600 border border-transparent rounded-md shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Next Section
            </button>
          ) : (
            <button
              onClick={handleSubmit}
              disabled={isSubmitting}
              className="px-6 py-3 text-base font-medium text-white bg-green-600 border border-transparent rounded-md shadow-sm hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isSubmitting ? 'Submitting...' : 'Submit Assessment'}
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default AssessmentForm;
