import React from 'react';
import { useDroppable } from '@dnd-kit/core';
import { SortableContext, verticalListSortingStrategy } from '@dnd-kit/sortable';
import { Candidate } from '../../types';
import KanbanCandidateCard from './KanbanCandidateCard';

// Create enhanced candidate interface locally
interface EnhancedCandidate extends Candidate {
  jobTitle?: string;
  experience?: string;
  location?: string;
  skills?: string[];
}

interface StageColumnProps {
  stage: Candidate['stage'];
  candidates: EnhancedCandidate[];
  stageLabel: string;
  stageColor: string;
  stageDescription: string;
}

const StageColumn: React.FC<StageColumnProps> = ({
  stage,
  candidates,
  stageLabel,
  stageColor,
  stageDescription
}) => {
  const { isOver, setNodeRef } = useDroppable({
    id: stage,
  });

  const droppableStyle = {
    backgroundColor: isOver ? 'rgba(59, 130, 246, 0.05)' : 'transparent',
    borderColor: isOver ? 'rgb(59, 130, 246)' : 'rgba(255, 255, 255, 0.2)',
    transform: isOver ? 'scale(1.02)' : 'scale(1)',
  };

  return (
    <div className="flex-shrink-0 w-80">
      <div 
        id={stage}
        ref={setNodeRef}
        style={droppableStyle}
        className="bg-white/60 backdrop-blur-sm rounded-2xl border-2 border-dashed border-white/20 shadow-soft p-4 transition-all duration-200"
      >
        {/* Column Header */}
        <div className="mb-4">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center space-x-2">
              <div className={`w-3 h-3 rounded-full bg-gradient-to-r ${stageColor}`}></div>
              <h3 className="font-semibold text-gray-900">{stageLabel}</h3>
            </div>
            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-100 text-gray-800">
              {candidates.length}
            </span>
          </div>
          <p className="text-xs text-gray-500">{stageDescription}</p>
        </div>

        {/* Candidates List - This is the droppable area */}
        <div 
          className={`min-h-[500px] max-h-[600px] overflow-y-auto transition-all duration-200 ${
            isOver ? 'bg-blue-50 rounded-lg p-2' : ''
          }`}
        >
          <SortableContext items={candidates.map(c => c.id)} strategy={verticalListSortingStrategy}>
            {candidates.length === 0 ? (
              <div className={`text-center py-8 border-2 border-dashed rounded-xl transition-colors ${
                isOver ? 'border-blue-300 bg-blue-25' : 'border-gray-300'
              }`}>
                <div className="w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-3">
                  <svg className="w-6 h-6 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
                  </svg>
                </div>
                <p className="text-sm text-gray-500">No candidates</p>
                <p className="text-xs text-gray-400 mt-1">Drag candidates here</p>
              </div>
            ) : (
              <div className="space-y-3">
                {candidates.map((candidate) => (
                  <KanbanCandidateCard key={candidate.id} candidate={candidate} />
                ))}
              </div>
            )}
          </SortableContext>
        </div>

        {/* Add Candidate Button */}
        <button className="w-full mt-3 p-3 border-2 border-dashed border-gray-300 rounded-xl text-gray-500 hover:text-gray-700 hover:border-gray-400 transition-colors duration-150 flex items-center justify-center">
          <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
          </svg>
          Add Candidate
        </button>
      </div>
    </div>
  );
};

export default StageColumn;
