import React, { useEffect } from 'react';
import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
  DragEndEvent,
  DragStartEvent,
} from '@dnd-kit/core';
import {
  SortableContext,
  verticalListSortingStrategy,
  sortableKeyboardCoordinates,
} from '@dnd-kit/sortable';
import { useCandidates } from '../../hooks/useCandidates';
import SortableCandidateCard from './SortableCandidateCard';

const CandidatesList: React.FC = () => {
  const { 
    candidates, 
    loadCandidates, 
    updateCandidateStage, 
    filters, 
    pagination,
    loading 
  } = useCandidates();

  // Load candidates when component mounts or filters change
  useEffect(() => {
    loadCandidates({
      page: pagination.page,
      pageSize: pagination.pageSize,
      search: filters.search,
      stage: filters.stage,
    });
  }, [loadCandidates, pagination.page, pagination.pageSize, filters.search, filters.stage]);

  const sensors = useSensors(
    useSensor(PointerSensor, {
      activationConstraint: {
        distance: 8,
      },
    }),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  const handleDragStart = (event: DragStartEvent) => {
    document.body.style.cursor = 'grabbing';
  };

  const handleDragEnd = (event: DragEndEvent) => {
    document.body.style.cursor = '';
    
    const { active, over } = event;

    if (!over) return;

    const candidateId = active.id as string;
    const targetCandidateId = over.id as string;

    // Find the target candidate to get its stage
    const targetCandidate = candidates.find(c => c.id === targetCandidateId);
    if (targetCandidate && targetCandidate.id !== candidateId) {
      updateCandidateStage(candidateId, targetCandidate.stage);
    }
  };

  const handleDragCancel = () => {
    document.body.style.cursor = '';
  };

  const getStageBadgeColor = (stage: string) => {
    const colors = {
      applied: 'bg-blue-100 text-blue-800',
      screen: 'bg-yellow-100 text-yellow-800',
      tech: 'bg-purple-100 text-purple-800',
      offer: 'bg-green-100 text-green-800',
      hired: 'bg-emerald-100 text-emerald-800',
      rejected: 'bg-red-100 text-red-800',
    };
    return colors[stage as keyof typeof colors] || 'bg-gray-100 text-gray-800';
  };

  const getStageLabel = (stage: string) => {
    const labels = {
      applied: 'Applied',
      screen: 'Screening',
      tech: 'Technical',
      offer: 'Offer',
      hired: 'Hired',
      rejected: 'Rejected',
    };
    return labels[stage as keyof typeof labels] || stage;
  };

  if (loading) {
    return (
      <div className="bg-white rounded-2xl border border-gray-200 shadow-soft p-8">
        <div className="flex items-center justify-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          <span className="ml-3 text-gray-600">Loading candidates...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header with stats */}
      <div className="bg-white rounded-2xl border border-gray-200 shadow-soft p-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold text-gray-900">Candidates</h1>
            <p className="text-gray-600 mt-1">Manage and review candidate applications</p>
          </div>
          <div className="text-right">
            <div className="text-3xl font-bold text-gray-900">{candidates.length}</div>
            <div className="text-sm text-gray-500">Total Candidates</div>
          </div>
        </div>
      </div>

      {/* Candidates List with Drag & Drop */}
      <DndContext
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragStart={handleDragStart}
        onDragEnd={handleDragEnd}
        onDragCancel={handleDragCancel}
      >
        <div className="bg-white rounded-2xl border border-gray-200 shadow-soft overflow-hidden">
          {/* List Header */}
          <div className="bg-gray-50 px-6 py-4 border-b border-gray-200">
            <div className="flex items-center justify-between">
              <h3 className="text-lg font-semibold text-gray-900">
                Candidate List ({candidates.length})
              </h3>
              <p className="text-sm text-gray-500">
                Drag candidates to change their stage
              </p>
            </div>
          </div>

          {/* Candidates */}
          <div className="divide-y divide-gray-200">
            <SortableContext items={candidates.map(c => c.id)} strategy={verticalListSortingStrategy}>
              {candidates.map((candidate) => (
                <SortableCandidateCard
                  key={candidate.id}
                  candidate={candidate}
                  stageBadgeColor={getStageBadgeColor(candidate.stage)}
                  stageLabel={getStageLabel(candidate.stage)}
                />
              ))}
            </SortableContext>
          </div>

          {candidates.length === 0 && (
            <div className="text-center py-12">
              <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
                </svg>
              </div>
              <p className="text-gray-500 text-lg">No candidates found</p>
              <p className="text-gray-400 text-sm mt-1">Try adjusting your filters</p>
            </div>
          )}
        </div>
      </DndContext>
    </div>
  );
};

export default CandidatesList;
