import React, { useEffect, useState } from 'react';
import { useCandidates } from '../../hooks/useCandidates';
import VirtualizedList from './VirtualizedList';
import CandidateFilters from './CandidateFilters';
import KanbanBoard from './KanbanBoard';
import LoadingSpinner from '../common/LoadingSpinner';
import { useSelector } from 'react-redux';
import { RootState } from '../../store';

type ViewMode = 'list' | 'kanban';

const CandidatesList: React.FC = () => {
  const {
    candidates,
    loading,
    pagination,
    filters,
    loadCandidates,
    updateFilters,
  } = useCandidates();

  const { user } = useSelector((state: RootState) => state.auth);
  const [viewMode, setViewMode] = useState<ViewMode>('list');
  const [searchTerm, setSearchTerm] = useState(filters.search);

  useEffect(() => {
    loadCandidates({
      page: pagination.page,
      pageSize: pagination.pageSize,
      search: filters.search,
      stage: filters.stage,
    });
  }, [pagination.page, pagination.pageSize, filters.search, filters.stage]);

  const handleSearch = (search: string) => {
    setSearchTerm(search);
    updateFilters({ search });
  };

  const handleStageFilter = (stage: string) => {
    updateFilters({ stage });
  };

  const handleViewModeChange = (mode: ViewMode) => {
    setViewMode(mode);
  };

  // Enhanced candidate data with more details
  const enhancedCandidates = candidates.map(candidate => ({
    ...candidate,
    appliedDate: candidate.appliedDate || new Date().toISOString(),
    jobTitle: candidate.jobTitle || 'Software Engineer',
    experience: candidate.experience || '2-5 years',
    location: candidate.location || 'Remote',
    skills: candidate.skills || ['JavaScript', 'React', 'Node.js']
  }));

  const stats = {
    total: pagination.total,
    applied: enhancedCandidates.filter(c => c.stage === 'applied').length,
    screen: enhancedCandidates.filter(c => c.stage === 'screen').length,
    tech: enhancedCandidates.filter(c => c.stage === 'tech').length,
    offer: enhancedCandidates.filter(c => c.stage === 'offer').length,
    hired: enhancedCandidates.filter(c => c.stage === 'hired').length,
    rejected: enhancedCandidates.filter(c => c.stage === 'rejected').length,
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50/30 animate-fade-in">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between mb-8">
          <div className="flex-1 min-w-0">
            <h1 className="text-3xl font-bold text-gray-900 sm:text-4xl bg-gradient-to-r from-gray-900 to-gray-700 bg-clip-text text-transparent">
              Candidate Pipeline
            </h1>
            <p className="mt-2 text-lg text-gray-600 max-w-2xl">
              Manage and track candidate applications through the hiring process
            </p>
          </div>
          
          {/* View Mode Toggle */}
          <div className="mt-4 lg:mt-0 flex items-center space-x-4">
            <div className="flex bg-gray-100 rounded-xl p-1">
              <button
                onClick={() => handleViewModeChange('list')}
                className={`px-4 py-2 text-sm font-medium rounded-lg transition-all duration-200 ${
                  viewMode === 'list'
                    ? 'bg-white text-gray-900 shadow-sm'
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                <div className="flex items-center">
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 10h16M4 14h16M4 18h16" />
                  </svg>
                  List View
                </div>
              </button>
              <button
                onClick={() => handleViewModeChange('kanban')}
                className={`px-4 py-2 text-sm font-medium rounded-lg transition-all duration-200 ${
                  viewMode === 'kanban'
                    ? 'bg-white text-gray-900 shadow-sm'
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                <div className="flex items-center">
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 17V7m0 10a2 2 0 01-2 2H5a2 2 0 01-2-2V7a2 2 0 012-2h2a2 2 0 012 2m0 10a2 2 0 002 2h2a2 2 0 002-2M9 7a2 2 0 012-2h2a2 2 0 012 2m0 10V7m0 10a2 2 0 002 2h2a2 2 0 002-2V7a2 2 0 00-2-2h-2a2 2 0 00-2 2" />
                  </svg>
                  Kanban Board
                </div>
              </button>
            </div>
          </div>
        </div>

        {/* Stats Grid */}
        <div className="grid grid-cols-2 gap-4 mb-8 sm:grid-cols-3 lg:grid-cols-7">
          {[
            { label: 'Total', value: stats.total, color: 'from-gray-500 to-gray-700' },
            { label: 'Applied', value: stats.applied, color: 'from-blue-500 to-cyan-500' },
            { label: 'Screen', value: stats.screen, color: 'from-yellow-500 to-orange-500' },
            { label: 'Technical', value: stats.tech, color: 'from-purple-500 to-pink-500' },
            { label: 'Offer', value: stats.offer, color: 'from-green-500 to-emerald-500' },
            { label: 'Hired', value: stats.hired, color: 'from-emerald-500 to-teal-500' },
            { label: 'Rejected', value: stats.rejected, color: 'from-red-500 to-pink-500' },
          ].map((stat, index) => (
            <div key={index} className="bg-white/80 backdrop-blur-sm rounded-2xl p-4 border border-white/20 shadow-soft">
              <div className="text-center">
                <p className="text-2xl font-bold text-gray-900">{stat.value}</p>
                <p className="text-sm text-gray-600 mt-1">{stat.label}</p>
              </div>
            </div>
          ))}
        </div>

        <div className="flex flex-col lg:flex-row gap-8">
          {/* Filters Sidebar */}
          <div className="lg:w-80 flex-shrink-0">
            <div className="sticky top-8">
              <CandidateFilters
                searchValue={searchTerm}
                stageValue={filters.stage}
                onSearch={handleSearch}
                onStageChange={handleStageFilter}
              />
            </div>
          </div>

          {/* Main Content */}
          <div className="flex-1">
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl border border-white/20 shadow-soft">
              {loading ? (
                <div className="flex justify-center items-center py-12">
                  <LoadingSpinner size="lg" />
                </div>
              ) : viewMode === 'list' ? (
                <VirtualizedList
                  candidates={enhancedCandidates}
                  height={600}
                  itemHeight={100}
                />
              ) : (
                <KanbanBoard candidates={enhancedCandidates} />
              )}
            </div>

            {/* Results Count */}
            <div className="mt-4 text-sm text-gray-600 flex justify-between items-center">
              <span>
                Showing <span className="font-semibold text-gray-900">{enhancedCandidates.length}</span> of{' '}
                <span className="font-semibold text-gray-900">{pagination.total}</span> candidates
              </span>
              {enhancedCandidates.length > 0 && (
                <button
                  onClick={() => {
                    updateFilters({ search: '', stage: '' });
                    setSearchTerm('');
                  }}
                  className="text-blue-600 hover:text-blue-700 font-medium text-sm"
                >
                  Clear all filters
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CandidatesList;
