import React from 'react';
import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
  DragEndEvent,
  DragStartEvent,
} from '@dnd-kit/core';
import {
  SortableContext,
  verticalListSortingStrategy,
  sortableKeyboardCoordinates,
} from '@dnd-kit/sortable';
import { Candidate } from '../../types';
import { useCandidates } from '../../hooks/useCandidates';
import SortableCandidateCard from './SortableCandidateCard';

interface CandidatesListProps {
  candidates: Candidate[];
}

const CandidatesList: React.FC<CandidatesListProps> = ({ candidates }) => {
  const { updateCandidateStage } = useCandidates();

  const sensors = useSensors(
    useSensor(PointerSensor, {
      activationConstraint: {
        distance: 8,
      },
    }),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  const handleDragStart = (event: DragStartEvent) => {
    document.body.style.cursor = 'grabbing';
  };

  const handleDragEnd = (event: DragEndEvent) => {
    document.body.style.cursor = '';
    
    const { active, over } = event;

    if (!over) return;

    const candidateId = active.id as string;
    const targetCandidateId = over.id as string;

    // Find the target candidate to get its stage
    const targetCandidate = candidates.find(c => c.id === targetCandidateId);
    if (targetCandidate) {
      updateCandidateStage(candidateId, targetCandidate.stage);
    }
  };

  const handleDragCancel = () => {
    document.body.style.cursor = '';
  };

  const getStageBadgeColor = (stage: Candidate['stage']) => {
    const colors = {
      applied: 'bg-blue-100 text-blue-800',
      screen: 'bg-yellow-100 text-yellow-800',
      tech: 'bg-purple-100 text-purple-800',
      offer: 'bg-green-100 text-green-800',
      hired: 'bg-emerald-100 text-emerald-800',
      rejected: 'bg-red-100 text-red-800',
    };
    return colors[stage];
  };

  const getStageLabel = (stage: Candidate['stage']) => {
    const labels = {
      applied: 'Applied',
      screen: 'Screening',
      tech: 'Technical',
      offer: 'Offer',
      hired: 'Hired',
      rejected: 'Rejected',
    };
    return labels[stage];
  };

  return (
    <DndContext
      sensors={sensors}
      collisionDetection={closestCenter}
      onDragStart={handleDragStart}
      onDragEnd={handleDragEnd}
      onDragCancel={handleDragCancel}
    >
      <div className="bg-white rounded-2xl border border-gray-200 shadow-soft overflow-hidden">
        {/* Header */}
        <div className="bg-gray-50 px-6 py-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-semibold text-gray-900">Candidates ({candidates.length})</h3>
            <p className="text-sm text-gray-500">Drag candidates to reorder within stages</p>
          </div>
        </div>

        {/* List */}
        <div className="divide-y divide-gray-200">
          <SortableContext items={candidates.map(c => c.id)} strategy={verticalListSortingStrategy}>
            {candidates.map((candidate) => (
              <SortableCandidateCard
                key={candidate.id}
                candidate={candidate}
                stageBadgeColor={getStageBadgeColor(candidate.stage)}
                stageLabel={getStageLabel(candidate.stage)}
              />
            ))}
          </SortableContext>
        </div>

        {candidates.length === 0 && (
          <div className="text-center py-12">
            <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
              </svg>
            </div>
            <p className="text-gray-500 text-lg">No candidates found</p>
            <p className="text-gray-400 text-sm mt-1">Try adjusting your filters</p>
          </div>
        )}
      </div>
    </DndContext>
  );
};

export default CandidatesList;
