import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import JobList from './JobList';
import JobFilters from './JobFilters';
import JobForm from './JobForm';
import { RootState } from '../../store';
import { fetchJobs } from '../../store/slices/jobsSlice';

const JobsBoard: React.FC = () => {
  const dispatch = useDispatch();
  const { jobs, loading, totalCount } = useSelector((state: RootState) => state.jobs);
  const { user } = useSelector((state: RootState) => state.auth);
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [filters, setFilters] = useState({
    search: '',
    status: '',
    tags: [] as string[],
    page: 1,
    pageSize: 12,
    sort: 'createdAt_desc'
  });

  useEffect(() => {
    // Simulate API call with mock data
    const mockJobs = [
      {
        id: '1',
        title: 'Senior Frontend Developer',
        company: 'TechCorp Inc',
        location: 'San Francisco, CA',
        salary: '$120,000 - $150,000',
        status: 'active' as const,
        description: 'We are looking for an experienced Frontend Developer to join our growing team. You will be responsible for building responsive web applications using modern technologies.',
        tags: ['React', 'TypeScript', 'Frontend', 'Remote'],
        applicants: 24,
        createdAt: new Date('2024-01-15').toISOString(),
      },
      {
        id: '2',
        title: 'Full Stack Engineer',
        company: 'StartupXYZ',
        location: 'New York, NY',
        salary: '$100,000 - $130,000',
