import React, { useState } from 'react';
import { useCandidates } from '../../hooks/useCandidates';
import MentionsInput from './MentionsInput';

interface NotesEditorProps {
  candidateId: string;
}

const NotesEditor: React.FC<NotesEditorProps> = ({ candidateId }) => {
  const { currentCandidate, addNote } = useCandidates();
  const [newNote, setNewNote] = useState('');

  const handleAddNote = async () => {
    if (!newNote.trim()) return;

    try {
      await addNote(candidateId, {
        content: newNote,
        author: 'Current User', // This would come from auth context
        mentions: extractMentions(newNote),
      });
      setNewNote('');
    } catch (error) {
      console.error('Failed to add note:', error);
    }
  };

  const extractMentions = (text: string): string[] => {
    const mentionRegex = /@(\w+)/g;
    const mentions: string[] = [];
    let match;
    while ((match = mentionRegex.exec(text)) !== null) {
      mentions.push(match[1]);
    }
    return mentions;
  };

  return (
    <div className="space-y-6">
      {/* Add New Note */}
      <div className="bg-white rounded-lg border border-gray-200 p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Add Note</h3>
        <div className="space-y-4">
          <MentionsInput
            value={newNote}
            onChange={setNewNote}
            placeholder="Add a note about this candidate. Use @ to mention team members..."
            rows={4}
          />
          <div className="flex justify-end">
            <button
              onClick={handleAddNote}
              disabled={!newNote.trim()}
              className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Add Note
            </button>
          </div>
        </div>
      </div>

      {/* Existing Notes */}
      <div className="space-y-4">
        <h3 className="text-lg font-medium text-gray-900">Previous Notes</h3>
        {currentCandidate?.notes && currentCandidate.notes.length > 0 ? (
          currentCandidate.notes.map((note) => (
            <div key={note.id} className="bg-white rounded-lg border border-gray-200 p-4">
              <div className="flex justify-between items-start mb-2">
                <span className="text-sm font-medium text-gray-900">{note.author}</span>
                <span className="text-sm text-gray-500">
                  {new Date(note.createdAt).toLocaleDateString()} at{' '}
                  {new Date(note.createdAt).toLocaleTimeString()}
                </span>
              </div>
              <p className="text-gray-700 whitespace-pre-wrap">{note.content}</p>
              {note.mentions.length > 0 && (
                <div className="mt-2">
                  <span className="text-xs text-gray-500">Mentioned: </span>
                  {note.mentions.map((mention, index) => (
                    <span
                      key={mention}
                      className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-blue-100 text-blue-800 mr-1"
                    >
                      @{mention}
                    </span>
                  ))}
                </div>
              )}
            </div>
          ))
        ) : (
          <div className="text-center py-8 text-gray-500">
            No notes yet. Add your first note above.
          </div>
        )}
      </div>
    </div>
  );
};

export default NotesEditor;
