import React from 'react';
import { Droppable, Draggable } from 'react-beautiful-dnd';
import { Candidate } from '../../types';
import KanbanCandidateCard from './KanbanCandidateCard';

interface StageColumnProps {
  stage: Candidate['stage'];
  candidates: Candidate[];
  stageLabel: string;
  stageColor: string;
}

const StageColumn: React.FC<StageColumnProps> = ({
  stage,
  candidates,
  stageLabel,
  stageColor,
}) => {
  return (
    <div className="flex-1 min-w-[300px] bg-gray-50 rounded-lg border border-gray-200">
      {/* Column Header */}
      <div className={`p-4 rounded-t-lg ${stageColor} text-white`}>
        <div className="flex justify-between items-center">
          <h3 className="font-semibold">{stageLabel}</h3>
          <span className="bg-white bg-opacity-20 px-2 py-1 rounded-full text-sm">
            {candidates.length}
          </span>
        </div>
      </div>

      {/* Droppable Area */}
      <Droppable droppableId={stage}>
        {(provided, snapshot) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            className={`p-2 min-h-[500px] transition-colors ${
              snapshot.isDraggingOver ? 'bg-blue-50 bg-opacity-50' : ''
            }`}
          >
            {candidates.map((candidate, index) => (
              <Draggable
                key={candidate.id}
                draggableId={candidate.id}
                index={index}
              >
                {(provided, snapshot) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    className={`mb-2 transition-transform ${
                      snapshot.isDragging ? 'rotate-5' : ''
                    }`}
                  >
                    <KanbanCandidateCard candidate={candidate} />
                  </div>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </div>
  );
};

export default StageColumn;
