import React from 'react';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { Candidate } from '../../types';

interface SortableCandidateCardProps {
  candidate: Candidate;
  stageBadgeColor: string;
  stageLabel: string;
}

const SortableCandidateCard: React.FC<SortableCandidateCardProps> = ({ 
  candidate, 
  stageBadgeColor, 
  stageLabel 
}) => {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: candidate.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.5 : 1,
  };

  const formatDate = (dateString: string | Date) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  return (
    <div
      ref={setNodeRef}
      style={style}
      {...attributes}
      {...listeners}
      className={`bg-white px-6 py-4 hover:bg-gray-50 transition-all duration-200 cursor-grab active:cursor-grabbing ${
        isDragging ? 'shadow-lg z-10' : ''
      }`}
    >
      <div className="flex items-center justify-between">
        {/* Candidate Info */}
        <div className="flex items-center space-x-4 flex-1">
          <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-cyan-500 rounded-full flex items-center justify-center text-white font-semibold">
            {candidate.name.charAt(0).toUpperCase()}
          </div>
          <div className="flex-1 min-w-0">
            <div className="flex items-center space-x-3">
              <h4 className="text-sm font-semibold text-gray-900 truncate">{candidate.name}</h4>
              <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${stageBadgeColor}`}>
                {stageLabel}
              </span>
            </div>
            <p className="text-sm text-gray-500 truncate">{candidate.email}</p>
            {candidate.jobTitle && (
              <p className="text-xs text-gray-400 mt-1">{candidate.jobTitle}</p>
            )}
          </div>
        </div>

        {/* Meta Info */}
        <div className="flex items-center space-x-6">
          <div className="text-right">
            <p className="text-sm text-gray-500">Applied</p>
            <p className="text-xs text-gray-400">{formatDate(candidate.appliedDate)}</p>
          </div>
          
          {/* Drag Handle */}
          <div className="flex items-center space-x-2 text-gray-400">
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 8h16M4 16h16" />
            </svg>
            <span className="text-xs">Drag</span>
          </div>
        </div>
      </div>

      {/* Skills (if available) */}
      {candidate.skills && candidate.skills.length > 0 && (
        <div className="mt-3 flex flex-wrap gap-1">
          {candidate.skills.slice(0, 4).map((skill, index) => (
            <span
              key={index}
              className="inline-block bg-gray-100 text-gray-700 text-xs px-2 py-1 rounded"
            >
              {skill}
            </span>
          ))}
          {candidate.skills.length > 4 && (
            <span className="inline-block bg-gray-100 text-gray-500 text-xs px-2 py-1 rounded">
              +{candidate.skills.length - 4} more
            </span>
          )}
        </div>
      )}
    </div>
  );
};

export default SortableCandidateCard;
