import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useCandidates } from '../../hooks/useCandidates';
import { useJobs } from '../../hooks/useJobs';
import Timeline from './Timeline';
import NotesEditor from './NotesEditor';
import LoadingSpinner from '../common/LoadingSpinner';

const CandidateProfile: React.FC = () => {
  const { candidateId } = useParams<{ candidateId: string }>();
  const { candidates, currentCandidate, loadCandidates, selectCandidate } = useCandidates();
  const { jobs } = useJobs();
  const [activeTab, setActiveTab] = useState<'profile' | 'timeline' | 'notes'>('profile');

  useEffect(() => {
    if (candidates.length === 0) {
      loadCandidates();
    }
  }, [candidates.length, loadCandidates]);

  useEffect(() => {
    if (candidateId && candidates.length > 0) {
      const candidate = candidates.find(c => c.id === candidateId);
      selectCandidate(candidate || null);
    }
  }, [candidateId, candidates, selectCandidate]);

  if (!currentCandidate) {
    return (
      <div className="flex justify-center items-center py-12">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  const candidateJob = jobs.find(job => job.id === currentCandidate.jobId);

  const getStageLabel = (stage: string) => {
    const labels: Record<string, string> = {
      applied: 'Applied',
      screen: 'Screen',
      tech: 'Technical',
      offer: 'Offer',
      hired: 'Hired',
      rejected: 'Rejected',
    };
    return labels[stage] || stage;
  };

  const getStageColor = (stage: string) => {
    const colors: Record<string, string> = {
      applied: 'bg-blue-100 text-blue-800',
      screen: 'bg-yellow-100 text-yellow-800',
      tech: 'bg-purple-100 text-purple-800',
      offer: 'bg-green-100 text-green-800',
      hired: 'bg-emerald-100 text-emerald-800',
      rejected: 'bg-red-100 text-red-800',
    };
    return colors[stage] || 'bg-gray-100 text-gray-800';
  };

  return (
    <div className="max-w-6xl mx-auto space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <Link
            to="/candidates"
            className="inline-flex items-center text-sm font-medium text-gray-500 hover:text-gray-700"
          >
            <svg className="mr-2 h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
            </svg>
            Back to Candidates
          </Link>
          <h1 className="mt-2 text-3xl font-bold text-gray-900">{currentCandidate.name}</h1>
        </div>
        <span
          className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${getStageColor(currentCandidate.stage)}`}
        >
          {getStageLabel(currentCandidate.stage)}
        </span>
      </div>

      {/* Tabs */}
      <div className="border-b border-gray-200">
        <nav className="-mb-px flex space-x-8">
          {[
            { id: 'profile', name: 'Profile' },
            { id: 'timeline', name: 'Timeline' },
            { id: 'notes', name: 'Notes' },
          ].map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id as any)}
              className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === tab.id
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              {tab.name}
            </button>
          ))}
        </nav>
      </div>

      {/* Tab Content */}
      <div className="bg-white shadow-sm rounded-lg border border-gray-200">
        {activeTab === 'profile' && (
          <div className="p-6">
            <div className="grid grid-cols-1 gap-6 lg:grid-cols-2">
              {/* Personal Information */}
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-4">Personal Information</h3>
                <dl className="grid grid-cols-1 gap-4">
                  <div>
                    <dt className="text-sm font-medium text-gray-500">Full Name</dt>
                    <dd className="mt-1 text-sm text-gray-900">{currentCandidate.name}</dd>
                  </div>
                  <div>
                    <dt className="text-sm font-medium text-gray-500">Email</dt>
                    <dd className="mt-1 text-sm text-gray-900">{currentCandidate.email}</dd>
                  </div>
                  {currentCandidate.phone && (
                    <div>
                      <dt className="text-sm font-medium text-gray-500">Phone</dt>
                      <dd className="mt-1 text-sm text-gray-900">{currentCandidate.phone}</dd>
                    </div>
                  )}
                  <div>
                    <dt className="text-sm font-medium text-gray-500">Applied Date</dt>
                    <dd className="mt-1 text-sm text-gray-900">
                      {new Date(currentCandidate.appliedDate).toLocaleDateString()} at{' '}
                      {new Date(currentCandidate.appliedDate).toLocaleTimeString()}
                    </dd>
                  </div>
                </dl>
              </div>

              {/* Application Information */}
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-4">Application Information</h3>
                <dl className="grid grid-cols-1 gap-4">
                  <div>
                    <dt className="text-sm font-medium text-gray-500">Current Stage</dt>
                    <dd className="mt-1">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStageColor(currentCandidate.stage)}`}>
                        {getStageLabel(currentCandidate.stage)}
                      </span>
                    </dd>
                  </div>
                  {candidateJob && (
                    <div>
                      <dt className="text-sm font-medium text-gray-500">Applied Position</dt>
                      <dd className="mt-1 text-sm text-gray-900">
                        <Link
                          to={`/jobs/${candidateJob.id}`}
                          className="text-blue-600 hover:text-blue-900"
                        >
                          {candidateJob.title}
                        </Link>
                      </dd>
                    </div>
                  )}
                  <div>
                    <dt className="text-sm font-medium text-gray-500">Candidate ID</dt>
                    <dd className="mt-1 text-sm text-gray-900">{currentCandidate.id}</dd>
                  </div>
                </dl>
              </div>
            </div>

            {/* Resume Section */}
            {currentCandidate.resume && (
              <div className="mt-8">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Resume</h3>
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-4 text-center">
                  <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                  </svg>
                  <p className="mt-2 text-sm text-gray-600">
                    Resume available for download
                  </p>
                  <button className="mt-2 inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500">
                    Download Resume
                  </button>
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'timeline' && (
          <div className="p-6">
            <Timeline candidateId={currentCandidate.id} />
          </div>
        )}

        {activeTab === 'notes' && (
          <div className="p-6">
            <NotesEditor candidateId={currentCandidate.id} />
          </div>
        )}
      </div>
    </div>
  );
};

export default CandidateProfile;
