import React, { useEffect, useState } from 'react';
import { useJobs } from '../../hooks/useJobs';
import JobList from './JobList';
import JobFilters from './JobFilters';
import JobForm from './JobForm';
import Pagination from '../common/Pagination';
import LoadingSpinner from '../common/LoadingSpinner';

const JobsBoard: React.FC = () => {
  const {
    jobs,
    loading,
    pagination,
    filters,
    loadJobs,
    updateFilters,
    updatePagination,
  } = useJobs();

  const [isJobFormOpen, setIsJobFormOpen] = useState(false);

  useEffect(() => {
    loadJobs({
      page: pagination.page,
      pageSize: pagination.pageSize,
      search: filters.search,
      status: filters.status,
    });
  }, [pagination.page, pagination.pageSize, filters.search, filters.status]);

  const handleSearch = (search: string) => {
    updateFilters({ search });
  };

  const handleStatusFilter = (status: string) => {
    updateFilters({ status });
  };

  const handlePageChange = (page: number) => {
    updatePagination({ page });
  };

  const handlePageSizeChange = (pageSize: number) => {
    updatePagination({ pageSize, page: 1 });
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Jobs Board</h1>
          <p className="text-gray-600">Manage and track your job postings</p>
        </div>
        <button
          onClick={() => setIsJobFormOpen(true)}
          className="px-4 py-2 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
        >
          Create Job
        </button>
      </div>

      {/* Filters */}
      <JobFilters
        searchValue={filters.search}
        statusValue={filters.status}
        onSearch={handleSearch}
        onStatusChange={handleStatusFilter}
      />

      {/* Jobs List */}
      <div className="bg-white shadow-sm rounded-lg border border-gray-200">
        {loading ? (
          <div className="flex justify-center items-center py-12">
            <LoadingSpinner size="lg" />
          </div>
        ) : (
          <>
            <JobList jobs={jobs} />
            <Pagination
              currentPage={pagination.page}
              totalPages={pagination.totalPages}
              totalItems={pagination.total}
              pageSize={pagination.pageSize}
              onPageChange={handlePageChange}
              onPageSizeChange={handlePageSizeChange}
            />
          </>
        )}
      </div>

      {/* Job Form Modal */}
      <JobForm
        isOpen={isJobFormOpen}
        onClose={() => setIsJobFormOpen(false)}
      />
    </div>
  );
};

export default JobsBoard;
