import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useJobs } from '../../hooks/useJobs';
import SearchBar from '../common/SearchBar';
import LoadingSpinner from '../common/LoadingSpinner';

const JobsBoard: React.FC = () => {
  const { jobs, loading, pagination, filters, loadJobs, updateFilters } = useJobs();
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    loadJobs({
      page: pagination.page,
      pageSize: pagination.pageSize,
      search: filters.search,
      status: 'active', // Only show active jobs to candidates
    });
  }, [pagination.page, pagination.pageSize, filters.search]);

  const handleSearch = (search: string) => {
    setSearchTerm(search);
    updateFilters({ search });
  };

  const activeJobs = jobs.filter(job => job.status === 'active');

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Available Positions
          </h1>
          <p className="text-lg text-gray-600">
            Find your next career opportunity
          </p>
        </div>

        {/* Search */}
        <div className="max-w-2xl mx-auto mb-8">
          <SearchBar
            onSearch={handleSearch}
            placeholder="Search jobs by title, company, or skills..."
            className="w-full"
          />
        </div>

        {/* Jobs Grid */}
        {loading ? (
          <div className="flex justify-center items-center py-12">
            <LoadingSpinner size="lg" />
          </div>
        ) : (
          <div className="grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-3">
            {activeJobs.map((job) => (
              <JobCard key={job.id} job={job} />
            ))}
          </div>
        )}

        {/* No Results */}
        {!loading && activeJobs.length === 0 && (
          <div className="text-center py-12">
            <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <h3 className="mt-2 text-sm font-medium text-gray-900">No jobs found</h3>
            <p className="mt-1 text-sm text-gray-500">
              Try adjusting your search criteria.
            </p>
          </div>
        )}

        {/* Results Count */}
        {activeJobs.length > 0 && (
          <div className="mt-8 text-center text-sm text-gray-600">
            Showing {activeJobs.length} of {pagination.total} jobs
          </div>
        )}
      </div>
    </div>
  );
};

const JobCard: React.FC<{ job: any }> = ({ job }) => {
  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition-shadow">
      <div className="p-6">
        <div className="flex items-start justify-between mb-4">
          <div className="flex items-center space-x-3">
            {job.brandLogo && (
              <img
                className="h-12 w-12 rounded-lg object-cover"
                src={job.brandLogo}
                alt={job.company}
              />
            )}
            <div>
              <h3 className="text-lg font-semibold text-gray-900 line-clamp-2">
                {job.title}
              </h3>
              <p className="text-gray-600">{job.company}</p>
            </div>
          </div>
        </div>

        <div className="space-y-2 mb-4">
          <div className="flex items-center text-sm text-gray-500">
            <svg className="flex-shrink-0 mr-2 h-4 w-4 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
            </svg>
            {job.location}
          </div>
          {job.salary && (
            <div className="flex items-center text-sm text-gray-500">
              <svg className="flex-shrink-0 mr-2 h-4 w-4 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1" />
              </svg>
              {job.salary}
            </div>
          )}
        </div>

        <div className="mb-4">
          <div className="flex flex-wrap gap-1">
            {job.tags.slice(0, 3).map((tag: string) => (
              <span
                key={tag}
                className="inline-flex items-center px-2 py-1 rounded text-xs font-medium bg-blue-100 text-blue-800"
              >
                {tag}
              </span>
            ))}
            {job.tags.length > 3 && (
              <span className="inline-flex items-center px-2 py-1 rounded text-xs font-medium bg-gray-100 text-gray-800">
                +{job.tags.length - 3} more
              </span>
            )}
          </div>
        </div>

        <p className="text-gray-600 text-sm line-clamp-3 mb-4">
          {job.description || 'No description available.'}
        </p>

        <div className="flex space-x-3">
          <Link
            to={`/jobs/${job.id}`}
            className="flex-1 text-center px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            View Details
          </Link>
          <Link
            to={`/application/${job.id}`}
            className="flex-1 text-center px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            Apply Now
          </Link>
        </div>
      </div>
    </div>
  );
};

export default JobsBoard;
