import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { RootState } from '../../store';

const Sidebar: React.FC = () => {
  const location = useLocation();
  const { user } = useSelector((state: RootState) => state.auth);

  const isActive = (path: string) => location.pathname === path;

  if (user?.role === 'hr') {
    return (
      <div className="w-64 bg-white shadow-lg">
        <div className="p-4">
          <h2 className="text-lg font-semibold text-gray-800">HR Portal</h2>
        </div>
        <nav className="mt-4">
          <Link
            to="/jobs"
            className={`flex items-center px-4 py-2 text-gray-700 hover:bg-gray-100 ${
              isActive('/jobs') ? 'bg-blue-100 text-blue-700 border-r-2 border-blue-700' : ''
            }`}
          >
            üìã Jobs Board
          </Link>
          <Link
            to="/candidates"
            className={`flex items-center px-4 py-2 text-gray-700 hover:bg-gray-100 ${
              isActive('/candidates') ? 'bg-blue-100 text-blue-700 border-r-2 border-blue-700' : ''
            }`}
          >
            üë• Candidates
          </Link>
          {/* Fix: Add assessments link that goes to a specific job or list */}
          <Link
            to="/assessments"
            className={`flex items-center px-4 py-2 text-gray-700 hover:bg-gray-100 ${
              location.pathname.startsWith('/assessments') ? 'bg-blue-100 text-blue-700 border-r-2 border-blue-700' : ''
            }`}
          >
            üìù Assessments
          </Link>
        </nav>
      </div>
    );
  }

  // Candidate sidebar
  return (
    <div className="w-64 bg-white shadow-lg">
      <div className="p-4">
        <h2 className="text-lg font-semibold text-gray-800">Candidate Portal</h2>
      </div>
      <nav className="mt-4">
        <Link
          to="/candidate-dashboard"
          className={`flex items-center px-4 py-2 text-gray-700 hover:bg-gray-100 ${
            isActive('/candidate-dashboard') ? 'bg-blue-100 text-blue-700 border-r-2 border-blue-700' : ''
            }`}
        >
          üè† Dashboard
        </Link>
        <Link
          to="/browse-jobs"
          className={`flex items-center px-4 py-2 text-gray-700 hover:bg-gray-100 ${
            isActive('/browse-jobs') ? 'bg-blue-100 text-blue-700 border-r-2 border-blue-700' : ''
            }`}
        >
          üíº Browse Jobs
        </Link>
        <Link
          to="/my-applications"
          className={`flex items-center px-4 py-2 text-gray-700 hover:bg-gray-100 ${
            isActive('/my-applications') ? 'bg-blue-100 text-blue-700 border-r-2 border-blue-700' : ''
            }`}
        >
          üìÑ My Applications
        </Link>
      </nav>
    </div>
  );
};

export default Sidebar;
