import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAppDispatch } from '../../hooks/redux';
import { login } from '../../store/slices/authSlice';

const Login: React.FC = () => {
  const dispatch = useAppDispatch();
  const navigate = useNavigate();

  const handleHRLogin = () => {
    dispatch(login({ role: 'hr' }));
    navigate('/jobs');
  };

  const handleCandidateLogin = () => {
    const candidateId = `candidate-${Math.floor(Math.random() * 1000) + 1}`;
    dispatch(login({ role: 'candidate', userId: candidateId }));
    navigate('/');
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            TalentFlow
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Sign in as HR or Candidate
          </p>
        </div>
        <div className="space-y-4">
          <button
            onClick={handleHRLogin}
            className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            HR Team Login
          </button>
          <button
            onClick={handleCandidateLogin}
            className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
          >
            Candidate Login
          </button>
        </div>
        <div className="mt-6 bg-yellow-50 border border-yellow-200 rounded-md p-4">
          <p className="text-sm text-yellow-800">
            <strong>Demo Credentials:</strong><br />
            • HR Team: Click "HR Team Login"<br />
            • Candidate: Click "Candidate Login" for random candidate access
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
