import React from 'react';
import { Assessment } from '../../types';
import QuestionRenderer from './QuestionRenderer';

interface AssessmentPreviewProps {
  assessment: Assessment;
  isLivePreview?: boolean;
}

const AssessmentPreview: React.FC<AssessmentPreviewProps> = ({ 
  assessment, 
  isLivePreview = false 
}) => {
  return (
    <div className={`p-6 ${isLivePreview ? 'bg-gray-50' : 'bg-white'}`}>
      {isLivePreview && (
        <div className="mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
          <h3 className="text-sm font-medium text-blue-800">Live Preview</h3>
          <p className="text-sm text-blue-600 mt-1">
            This is how candidates will see the assessment. Changes are updated in real-time.
          </p>
        </div>
      )}

      {/* Assessment Header */}
      <div className="bg-white rounded-lg border border-gray-200 p-6 mb-6">
        <h1 className="text-2xl font-bold text-gray-900 mb-2">{assessment.title}</h1>
        {assessment.description && (
          <p className="text-gray-600">{assessment.description}</p>
        )}
      </div>

      {/* Sections and Questions */}
      <div className="space-y-6">
        {assessment.sections.map((section, sectionIndex) => (
          <div key={section.id} className="bg-white rounded-lg border border-gray-200">
            {/* Section Header */}
            <div className="px-6 py-4 border-b border-gray-200 bg-gray-50">
              <h2 className="text-lg font-medium text-gray-900">
                {sectionIndex + 1}. {section.title}
              </h2>
            </div>

            {/* Questions */}
            <div className="p-6 space-y-6">
              {section.questions.map((question, questionIndex) => (
                <QuestionRenderer
                  key={question.id}
                  question={question}
                  questionNumber={questionIndex + 1}
                  isPreview={true}
                  onChange={() => {}}
                  value={null}
                />
              ))}

              {section.questions.length === 0 && (
                <div className="text-center py-8 text-gray-500">
                  No questions in this section
                </div>
              )}
            </div>
          </div>
        ))}

        {assessment.sections.length === 0 && (
          <div className="text-center py-12 bg-white rounded-lg border border-gray-200">
            <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
            </svg>
            <h3 className="mt-2 text-sm font-medium text-gray-900">No sections yet</h3>
            <p className="mt-1 text-sm text-gray-500">
              Add sections and questions to build your assessment.
            </p>
          </div>
        )}
      </div>

      {/* Submit Button (in preview mode) */}
      {!isLivePreview && assessment.sections.length > 0 && (
        <div className="mt-8 pt-6 border-t border-gray-200">
          <button
            type="button"
            className="w-full px-4 py-3 text-base font-medium text-white bg-blue-600 border border-transparent rounded-md shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            Submit Assessment
          </button>
        </div>
      )}
    </div>
  );
};

export default AssessmentPreview;
