import React, { useEffect, useState, useCallback } from 'react';
import { useJobs } from '../../hooks/useJobs';
import JobList from './JobList';
import JobFilters from './JobFilters';
import JobForm from './JobForm';
import Pagination from '../common/Pagination';
import LoadingSpinner from '../common/LoadingSpinner';
import { useSelector } from 'react-redux';
import { RootState } from '../../store';

// Debounce function
const debounce = <T extends (...args: any[]) => any>(
  func: T,
  wait: number
): ((...args: Parameters<T>) => void) => {
  let timeout: NodeJS.Timeout;
  
  return (...args: Parameters<T>) => {
    clearTimeout(timeout);
    timeout = setTimeout(() => func(...args), wait);
  };
};

const JobsBoard: React.FC = () => {
  const {
    jobs,
    loading,
    pagination,
    filters,
    loadJobs,
    updateFilters,
    updatePagination,
  } = useJobs();

  const { user } = useSelector((state: RootState) => state.auth);
  const [isJobFormOpen, setIsJobFormOpen] = useState(false);
  const [searchValue, setSearchValue] = useState(filters.search);
  const [hasLoadedAll, setHasLoadedAll] = useState(false);

  // Debounced search to avoid too many API calls
  const debouncedSearch = useCallback(
    debounce((search: string) => {
      updateFilters({ search });
    }, 500),
    [updateFilters]
  );

  // Load all jobs on initial mount for better pagination performance
  useEffect(() => {
    const initializeJobs = async () => {
      try {
        await loadJobs({ 
          page: 1, 
          pageSize: 1000, 
          loadAll: true,
          status: filters.status 
        });
        setHasLoadedAll(true);
      } catch (error) {
        console.error('Failed to load jobs:', error);
      }
    };

    initializeJobs();
  }, []); // Empty dependency array - only run once on mount

  // Handle pagination changes when using client-side pagination
  useEffect(() => {
    if (hasLoadedAll) {
      // When using client-side pagination, we don't need to reload from API
      // The Redux slice will handle the pagination automatically
      console.log('Client-side pagination - page:', pagination.page);
    } else {
      // If not using client-side pagination, load from API
      const loadJobsWithParams = async () => {
        try {
          await loadJobs({
            page: pagination.page,
            pageSize: pagination.pageSize,
            search: filters.search,
            status: filters.status,
          });
        } catch (error) {
          console.error('Failed to load jobs:', error);
        }
      };

      loadJobsWithParams();
    }
  }, [pagination.page, pagination.pageSize, hasLoadedAll]);

  // Handle filter changes
  useEffect(() => {
    if (hasLoadedAll) {
      // Client-side filtering - handled automatically by Redux
      console.log('Client-side filtering - search:', filters.search, 'status:', filters.status);
    } else {
      // Server-side filtering - load from API
      const loadJobsWithFilters = async () => {
        try {
          await loadJobs({
            page: 1, // Reset to first page when filters change
            pageSize: pagination.pageSize,
            search: filters.search,
            status: filters.status,
          });
        } catch (error) {
          console.error('Failed to load jobs with filters:', error);
        }
      };

      loadJobsWithFilters();
    }
  }, [filters.search, filters.status, hasLoadedAll]);

  const handleSearch = (search: string) => {
    setSearchValue(search);
    debouncedSearch(search);
  };

  const handleStatusFilter = (status: string) => {
    updateFilters({ status });
  };

  const handlePageChange = (page: number) => {
    console.log('Changing to page:', page);
    updatePagination({ page });
    // Smooth scroll to top when changing pages
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handlePageSizeChange = (pageSize: number) => {
    updatePagination({ pageSize, page: 1 });
  };

  // Mock stats for enhanced UI
  const stats = {
    active: jobs.filter(job => job.status === 'active').length,
    archived: jobs.filter(job => job.status === 'archived').length,
    total: pagination.total,
    featured: jobs.filter(job => job.tags?.includes('featured')).length
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50/30 animate-fade-in">
      {/* Header Section */}
      <div className="relative bg-white/80 backdrop-blur-sm border-b border-gray-200/60">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
            <div className="flex-1 min-w-0">
              <h1 className="text-3xl font-bold text-gray-900 sm:text-4xl bg-gradient-to-r from-gray-900 to-gray-700 bg-clip-text text-transparent">
                Jobs Board
              </h1>
              <p className="mt-2 text-lg text-gray-600 max-w-2xl">
                {user?.role === 'hr' 
                  ? 'Manage and track all job positions in your organization' 
                  : 'Discover your next career opportunity'
                }
              </p>
            </div>
            
            {user?.role === 'hr' && (
              <div className="mt-4 lg:mt-0 lg:ml-4">
                <button
                  onClick={() => setIsJobFormOpen(true)}
                  className="inline-flex items-center px-6 py-3 border border-transparent text-base font-medium rounded-xl shadow-lg focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white transition-all duration-200 transform hover:-translate-y-0.5 hover:shadow-xl"
                >
                  <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                  </svg>
                  Create New Job
                </button>
              </div>
            )}
          </div>

          {/* Stats Grid */}
          <div className="mt-8 grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
            <div className="bg-white/60 backdrop-blur-sm rounded-2xl p-6 border border-white/20 shadow-soft hover:shadow-glow transition-all duration-300">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <div className="w-12 h-12 bg-gradient-to-r from-green-500 to-emerald-500 rounded-xl flex items-center justify-center">
                    <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2 2v2m4 6h.01M5 20h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                    </svg>
                  </div>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Active Jobs</p>
                  <p className="text-2xl font-bold text-gray-900">{stats.active}</p>
                </div>
              </div>
            </div>

            <div className="bg-white/60 backdrop-blur-sm rounded-2xl p-6 border border-white/20 shadow-soft hover:shadow-glow transition-all duration-300">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-cyan-500 rounded-xl flex items-center justify-center">
                    <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 8h14M5 8a2 2 0 110-4h14a2 2 0 110 4M5 8v10a2 2 0 002 2h10a2 2 0 002-2V8m-9 4h4" />
                    </svg>
                  </div>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Archived</p>
                  <p className="text-2xl font-bold text-gray-900">{stats.archived}</p>
                </div>
              </div>
            </div>

            <div className="bg-white/60 backdrop-blur-sm rounded-2xl p-6 border border-white/20 shadow-soft hover:shadow-glow transition-all duration-300">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <div className="w-12 h-12 bg-gradient-to-r from-purple-500 to-pink-500 rounded-xl flex items-center justify-center">
                    <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
                    </svg>
                  </div>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Featured</p>
                  <p className="text-2xl font-bold text-gray-900">{stats.featured}</p>
                </div>
              </div>
            </div>

            <div className="bg-white/60 backdrop-blur-sm rounded-2xl p-6 border border-white/20 shadow-soft hover:shadow-glow transition-all duration-300">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <div className="w-12 h-12 bg-gradient-to-r from-orange-500 to-red-500 rounded-xl flex items-center justify-center">
                    <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                  </div>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Total</p>
                  <p className="text-2xl font-bold text-gray-900">{stats.total}</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Filters Sidebar */}
          <div className="lg:w-80 flex-shrink-0">
            <div className="sticky top-8">
              <JobFilters
                searchValue={searchValue}
                statusValue={filters.status}
                onSearch={handleSearch}
                onStatusChange={handleStatusFilter}
              />
            </div>
          </div>

          {/* Jobs List */}
          <div className="flex-1">
            <div className="bg-white/60 backdrop-blur-sm rounded-2xl border border-white/20 shadow-soft">
              {loading && !hasLoadedAll ? (
                <div className="flex justify-center items-center py-12">
                  <LoadingSpinner size="lg" />
                </div>
              ) : (
                <>
                  <JobList jobs={jobs} />
                  <div className="px-6 py-4 border-t border-gray-200/60">
                    <Pagination
                      currentPage={pagination.page}
                      totalPages={pagination.totalPages}
                      totalItems={pagination.total}
                      pageSize={pagination.pageSize}
                      onPageChange={handlePageChange}
                      onPageSizeChange={handlePageSizeChange}
                    />
                  </div>
                </>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Job Form Modal */}
      <JobForm
        isOpen={isJobFormOpen}
        onClose={() => setIsJobFormOpen(false)}
      />
    </div>
  );
};

export default JobsBoard;
