import React from 'react';
import { Question } from '../../types';

interface QuestionRendererProps {
  question: Question;
  questionNumber: number;
  isPreview: boolean;
  onChange: (value: any) => void;
  value: any;
}

const QuestionRenderer: React.FC<QuestionRendererProps> = ({
  question,
  questionNumber,
  isPreview,
  onChange,
  value,
}) => {
  const renderQuestionInput = () => {
    switch (question.type) {
      case 'short-text':
        return (
          <input
            type="text"
            value={value || ''}
            onChange={(e) => onChange(e.target.value)}
            disabled={isPreview}
            className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm px-3 py-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter your answer"
          />
        );

      case 'long-text':
        return (
          <textarea
            value={value || ''}
            onChange={(e) => onChange(e.target.value)}
            disabled={isPreview}
            rows={4}
            className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm px-3 py-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter your answer"
          />
        );

      case 'single-choice':
        return (
          <div className="mt-1 space-y-2">
            {question.options?.map((option, index) => (
              <div key={index} className="flex items-center">
                <input
                  type="radio"
                  id={`${question.id}-${index}`}
                  name={question.id}
                  value={option}
                  checked={value === option}
                  onChange={(e) => onChange(e.target.value)}
                  disabled={isPreview}
                  className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300"
                />
                <label
                  htmlFor={`${question.id}-${index}`}
                  className="ml-3 block text-sm text-gray-700"
                >
                  {option}
                </label>
              </div>
            ))}
          </div>
        );

      case 'multi-choice':
        return (
          <div className="mt-1 space-y-2">
            {question.options?.map((option, index) => (
              <div key={index} className="flex items-center">
                <input
                  type="checkbox"
                  id={`${question.id}-${index}`}
                  checked={Array.isArray(value) && value.includes(option)}
                  onChange={(e) => {
                    const currentValues = Array.isArray(value) ? value : [];
                    if (e.target.checked) {
                      onChange([...currentValues, option]);
                    } else {
                      onChange(currentValues.filter((v: string) => v !== option));
                    }
                  }}
                  disabled={isPreview}
                  className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                />
                <label
                  htmlFor={`${question.id}-${index}`}
                  className="ml-3 block text-sm text-gray-700"
                >
                  {option}
                </label>
              </div>
            ))}
          </div>
        );

      case 'numeric':
        return (
          <input
            type="number"
            value={value || ''}
            onChange={(e) => onChange(e.target.value)}
            disabled={isPreview}
            min={question.min}
            max={question.max}
            className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm px-3 py-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter a number"
          />
        );

      case 'file':
        return (
          <div className="mt-1">
            <input
              type="file"
              onChange={(e) => onChange(e.target.files?.[0])}
              disabled={isPreview}
              className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-medium file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
            />
            <p className="mt-1 text-xs text-gray-500">
              {isPreview ? 'File upload will be available to candidates' : 'Upload your file'}
            </p>
          </div>
        );

      default:
        return <div>Unsupported question type</div>;
    }
  };

  return (
    <div className="bg-white p-4 rounded-lg border border-gray-200">
      <div className="flex items-start space-x-3">
        <span className="flex-shrink-0 w-6 h-6 bg-blue-100 text-blue-800 rounded-full text-sm font-medium flex items-center justify-center">
          {questionNumber}
        </span>
        <div className="flex-1 min-w-0">
          <label className="block text-sm font-medium text-gray-700">
            {question.question}
            {question.required && <span className="text-red-500 ml-1">*</span>}
          </label>
          
          {renderQuestionInput()}

          {/* Validation hints */}
          {question.type === 'numeric' && (question.min !== undefined || question.max !== undefined) && (
            <p className="mt-1 text-xs text-gray-500">
              {question.min !== undefined && `Min: ${question.min}`}
              {question.min !== undefined && question.max !== undefined && ', '}
              {question.max !== undefined && `Max: ${question.max}`}
            </p>
          )}

          {(question.type === 'short-text' || question.type === 'long-text') && question.maxLength && (
            <p className="mt-1 text-xs text-gray-500">
              Maximum {question.maxLength} characters
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default QuestionRenderer;
